sys_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
   var startDate = g_form.getValue("u_start_date");
	var endDate = g_form.getValue("u_end_date");
	var format = g_user_date_format;
	var isEndBeforeStart = compareDates (startDate, format, endDate, format);
	var days = 0;
	if(isEndBeforeStart){
		g_form.setValue("u_end_date",  '');
		g_form.addErrorMessage("End Date must be after Start Date");
	} 
	else if(startDate.getDay != 0 || startDate.getDay != 6 ) {
		days++;
	}
	else{
		alert("You must not be choose weekends");
	}

}
